'use strict';

var https = require('https');
var httpsOptions = require('./').merge({}); // works will localhost.daplie.me, localhost.foo.daplie.me, etc
var server = https.createServer(httpsOptions);
var port = process.argv[2] || 8443;

server.on('request', function (req, res) {
  res.end('[' + req.method + ']' + ' ' + req.url);
});
server.listen(port, function () {
  console.log('Listening', server.address());
  console.log('<https://localhost.daplie.me:' + server.address().port + '/>');
});
